import  { createGlobalStyle }  from 'styled-components';
import theme  from './theme';

const GlobalStyle = createGlobalStyle`

body{
  padding:0;
  margin:0;
  background-color:${({theme} : {theme: any}) => theme.mainColor}; ;
  height: 100vh;
  border: solid 1px ${({theme} : {theme: any}) => theme.DetailColor};;
}

div{
  width: 100%;
  height: 8rem;
  box-sizing: border-box;
  border-bottom : 1px solid ${({theme} : {theme: any}) => theme.DetailColor};;

}
`
export default GlobalStyle ;
